// aka a keysend
export type TRANSACTION_STATUS =
  | "TRANSACTION_STATUS_COMPLETED"
  | "TRANSACTION_STATUS_ERROR"
  | "TRANSACTION_STATUS_EXPIRED"
  | "TRANSACTION_STATUS_FAILED"
  | "TRANSACTION_STATUS_PENDING"
  | "TRANSACTION_STATUS_PROCESSING";

// aka an invoice paid by ZBD
export type PAYMENT_STATUS = "completed" | "error" | "pending" | "processing";

// aka an invoice generated by ZBD
export type CHARGE_STATUS =
  | "completed"
  | "error"
  | "pending"
  | "processing"
  | "expired";
